class Particle {
  constructor(x, y, h) {
    this.pos = createVector(x, y);
    this.vel = p5.Vector.random2D().mult(random(1, 4));
    this.acc = createVector(0, 0.05);
    this.lifespan = 100;
    this.baseHue = h;
  }

  isFinished() {
    return this.lifespan < 0;
  }

  update() {
    this.vel.add(this.acc);
    this.pos.add(this.vel);
    this.lifespan -= 2;
  }

  show() {
    let currentHue = (this.baseHue + (100 - this.lifespan) * 0.5) % 360;
    let saturation = this.lifespan;
    let brightness = 100;
    fill(currentHue, saturation, brightness, this.lifespan);
    let d = map(this.lifespan, 0, 100, 0, 20);
    ellipse(this.pos.x, this.pos.y, d, d);
  }
}

