class Jelly {
  constructor(x, y) {
    this.pos = createVector(x, y);
    this.vel = createVector(0, -1);
    this.noiseOffsetX = random(1000);
    this.bodySize = random(20, 50);
    this.hue = random(180, 240);
    this.tentacles = [];
    for (let i = 0; i < 8; i++) {
      this.tentacles.push(new Tentacle(this.pos, this.bodySize));
    }
  }

  update() {
    let dirX = map(noise(this.noiseOffsetX), 0, 1, -0.5, 0.5);
    this.vel.x = dirX;
    this.pos.add(this.vel);
    this.noiseOffsetX += 0.01;

    if (this.pos.y < -this.bodySize) this.pos.y = height + this.bodySize;

    for (let t of this.tentacles) {
      t.update(this.pos);
    }
  }

  display() {
    noStroke();
    fill(this.hue, 50, 100, 40);
    ellipse(this.pos.x, this.pos.y, this.bodySize, this.bodySize * 1.2);
    for (let t of this.tentacles) {
      t.display(this.hue);
    }
  }
}

class Tentacle {
  constructor(parentPos, parentSize) {
    this.segments = [];
    this.numSegments = 15;
    for (let i = 0; i < this.numSegments; i++) {
      this.segments.push(parentPos.copy());
    }
    this.len = parentSize / this.numSegments;
  }

  update(parentPos) {
    this.segments[0] = parentPos.copy();
    for (let i = 1; i < this.numSegments; i++) {
      let prev = this.segments[i - 1];
      let current = this.segments[i];
      let angle = atan2(current.y - prev.y, current.x - prev.x);
      current.x = prev.x + cos(angle) * this.len;
      current.y = prev.y + sin(angle) * this.len + 0.5;
    }
  }

  display(hue) {
    stroke(hue, 60, 100, 60);
    strokeWeight(2);
    noFill();
    beginShape();
    for (let seg of this.segments) {
      vertex(seg.x, seg.y);
    }
    endShape();
  }
}

