const sketch = (p) => {
  let streams = [];
  let symbolSize = 20;

  p.setup = () => {
    p.createCanvas(800, 1200);
    let x = 0;
    for (let i = 0; i <= p.width / symbolSize; i++) {
      let stream = new Stream();
      stream.generateSymbols(x, p.random(-1000, 0));
      streams.push(stream);
      x += symbolSize;
    }
    p.textSize(symbolSize);
    p.textFont('monospace');
  };

  p.draw = () => {
    p.background(0, 150);
    streams.forEach(stream => {
      stream.render();
    });
  };

  class Symbol {
    constructor(x, y, speed, first) {
      this.x = x;
      this.y = y;
      this.speed = speed;
      this.first = first;
      this.switchInterval = p.round(p.random(2, 25));
      // 直接在构造函数中无条件地为符号赋一个初始值。
      this.value = String.fromCharCode(0x30A0 + p.round(p.random(0, 96)));
    }

    // 这个方法现在只用于在动画循环中“更新”字符
    setToRandomSymbol() {
      if (p.frameCount % this.switchInterval == 0) {
        this.value = String.fromCharCode(0x30A0 + p.round(p.random(0, 96)));
      }
    }
  }

  class Stream {
    constructor() {
      this.symbols = [];
      this.totalSymbols = p.round(p.random(5, 35));
      this.speed = p.random(5, 20);
      this.x = 0;
    }

    generateSymbols(x, y) {
      this.x = x;
      this.symbols = [];
      let first = p.round(p.random(0, 4)) == 1;
      for (let i = 0; i <= this.totalSymbols; i++) {
        let symbol = new Symbol(this.x, y, this.speed, first);
        this.symbols.push(symbol);
        y -= symbolSize;
        first = false;
      }
    }

    render() {
      this.symbols.forEach(symbol => {
        if (symbol.first) {
          p.fill(180, 255, 180);
        } else {
          p.fill(0, 255, 70);
        }
        p.text(symbol.value, symbol.x, symbol.y);
        symbol.y += symbol.speed;
        symbol.setToRandomSymbol(); // 调用更新
      });

      let lastSymbol = this.symbols[this.symbols.length - 1];
      if (lastSymbol.y > p.height) {
        this.generateSymbols(this.x, p.random(-500, 0));
      }
    }
  }
};

