// Flock 类，用于管理所有鸟类的数组
class Flock {
  constructor() {
    // 初始化鸟类数组
    this.boids = [];
  }

  run() {
    for (let boid of this.boids) {
      // 将整个鸟类列表传递给每只鸟
      boid.run(this.boids);
    }
  }

  addBoid(b) {
    this.boids.push(b);
  }
}

class Boid {
  constructor(x, y) {
    this.acceleration = createVector(0, 0);
    this.velocity = createVector(random(-1, 1), random(-1, 1));
    this.position = createVector(x, y);
    this.size = 3.0;

    // 最大速度
    this.maxSpeed = 3;

    // 最大转向力
    this.maxForce = 0.05;
    colorMode(HSB);
    this.color = color(random(256), 255, 255);
  }

  run(boids) {
    this.flock(boids);
    this.update();
    this.borders();
    this.render();
  }

  applyForce(force) {
    // 我们可以在这里添加质量：A = F / M
    this.acceleration.add(force);
  }

  // 每次根据三个规则积累新的加速度
  flock(boids) {
    let separation = this.separate(boids);
    let alignment = this.align(boids);
    let cohesion = this.cohesion(boids);

    // 随意加权这些力
    separation.mult(1.5);
    alignment.mult(1.0);
    cohesion.mult(1.0);

    // 将这些力向量添加到加速度中
    this.applyForce(separation);
    this.applyForce(alignment);
    this.applyForce(cohesion);
  }

  // 更新位置的方法
  update() {
    // 更新速度
    this.velocity.add(this.acceleration);

    // 限制速度
    this.velocity.limit(this.maxSpeed);
    this.position.add(this.velocity);

    // 每次循环后重置加速度为 0
    this.acceleration.mult(0);
  }

  // 计算并应用指向目标的转向力
  // 转向 = 期望 - 速度
  seek(target) {
    // 一个指向目标位置的向量
    let desired = p5.Vector.sub(target, this.position);

    // 归一化期望并缩放至最大速度
    desired.normalize();
    desired.mult(this.maxSpeed);

    // 转向 = 期望 - 速度
    let steer = p5.Vector.sub(desired, this.velocity);

    // 限制最大转向力
    steer.limit(this.maxForce);
    return steer;
  }

  render() {
    // 绘制旋转的三角形，方向与速度一致
    let theta = this.velocity.heading() + radians(90);
    fill(this.color);
    stroke(255);
    push();
    translate(this.position.x, this.position.y);
    rotate(theta);
    beginShape();
    vertex(0, -this.size * 2);
    vertex(-this.size, this.size * 2);
    vertex(this.size, this.size * 2);
    endShape(CLOSE);
    pop();
  }

  // 边界处理
  borders() {
    if (this.position.x < -this.size) {
      this.position.x = width + this.size;
    }

    if (this.position.y < -this.size) {
      this.position.y = height + this.size;
    }

    if (this.position.x > width + this.size) {
      this.position.x = -this.size;
    }

    if (this.position.y > height + this.size) {
      this.position.y = -this.size;
    }
  }

  // 分离
  // 该方法检查附近的鸟类并避免它们过于接近
  separate(boids) {
    let desiredSeparation = 25.0;
    let steer = createVector(0, 0);
    let count = 0;

    // 对每一只鸟类，检查它是否太近
    for (let boid of boids) {
      let distanceToNeighbor = p5.Vector.dist(this.position, boid.position);

      // 如果距离大于 0 且小于一个任意的数值（自己除外）
      if (distanceToNeighbor > 0 && distanceToNeighbor < desiredSeparation) {
        // 计算指向远离邻居的向量
        let diff = p5.Vector.sub(this.position, boid.position);
        diff.normalize();

        // 按距离缩放
        diff.div(distanceToNeighbor);
        steer.add(diff);

        // 记录有多少个鸟类
        count++;
      }
    }

    // 平均 -- 除以数量
    if (count > 0) {
      steer.div(count);
    }

    // 如果向量大于 0
    if (steer.mag() > 0) {
      // 实现雷诺兹的公式：转向 = 期望 - 速度
      steer.normalize();
      steer.mult(this.maxSpeed);
      steer.sub(this.velocity);
      steer.limit(this.maxForce);
    }
    return steer;
  }

  // 对齐
  // 对每只附近的鸟类，计算平均速度
  align(boids) {
    let neighborDistance = 50;
    let sum = createVector(0, 0);
    let count = 0;
    for (let i = 0; i < boids.length; i++) {
      let d = p5.Vector.dist(this.position, boids[i].position);
      if (d > 0 && d < neighborDistance) {
        sum.add(boids[i].velocity);
        count++;
      }
    }
    if (count > 0) {
      sum.div(count);
      sum.normalize();
      sum.mult(this.maxSpeed);
      let steer = p5.Vector.sub(sum, this.velocity);
      steer.limit(this.maxForce);
      return steer;
    } else {
      return createVector(0, 0);
    }
  }

  // 聚合
  // 对所有附近鸟类的平均位置（即中心）计算指向该位置的转向向量
  cohesion(boids) {
    let neighborDistance = 50;
    let sum = createVector(0, 0); // 从空向量开始累积所有位置
    let count = 0;
    for (let i = 0; i < boids.length; i++) {
      let d = p5.Vector.dist(this.position, boids[i].position);
      if (d > 0 && d < neighborDistance) {
        sum.add(boids[i].position); // 添加位置
        count++;
      }
    }
    if (count > 0) {
      sum.div(count);
      return this.seek(sum); // 指向该位置
    } else {
      return createVector(0, 0);
    }
  }
} // class Boid

// 声明物理计算的变量
let centerX = 0.0;
let centerY = 0.0;
let radius = 45;
let rotAngle = -90;
let accelX = 0.0;
let accelY = 0.0;
let deltaX = 0.0;
let deltaY = 0.0;
let springing = 0.0009;
let damping = 0.98;

// 声明指定顶点位置的变量
let nodes = 5;
let nodeStartX = [];
let nodeStartY = [];
let nodeX = [];
let nodeY = [];
let angle = [];
let frequency = [];

// 声明曲线紧密度的变量
let organicConstant = 1.0;

function setup() {
  createCanvas(710, 400);

  // 从画布中心开始
  centerX = width / 2;
  centerY = height / 2;

  // 初始化数组为0
  for (let i = 0; i < nodes; i++) {
    nodeStartX[i] = 0;
    nodeStartY[i] = 0;
    nodeX[i] = 0;
    nodeY[i] = 0;
    angle[i] = 0;
  }

  // 初始化角落节点的频率
  for (let i = 0; i < nodes; i++) {
    frequency[i] = random(5, 12);
  }

  noStroke();
  angleMode(DEGREES);
}

function draw() {
  // 使用 alpha 混合实现渐变效果
  background(0, 50);

  // 绘制并移动形状
  drawShape();
  moveShape();
}

function drawShape() {
  // 计算节点的起始位置
  for (let i = 0; i < nodes; i++) {
    nodeStartX[i] = centerX + cos(rotAngle) * radius;
    nodeStartY[i] = centerY + sin(rotAngle) * radius;
    rotAngle += 360.0 / nodes;
  }

  // 绘制多边形

  curveTightness(organicConstant);
  let shapeColor = lerpColor(color('red'), color('yellow'), organicConstant);
  fill(shapeColor);

  beginShape();
  for (let i = 0; i < nodes; i++) {
    curveVertex(nodeX[i], nodeY[i]);
  }
  endShape(CLOSE);
}

function moveShape() {
  // 移动中心点
  deltaX = mouseX - centerX;
  deltaY = mouseY - centerY;

  // 创建弹簧效果
  deltaX *= springing;
  deltaY *= springing;
  accelX += deltaX;
  accelY += deltaY;

  // 移动中心
  centerX += accelX;
  centerY += accelY;

  // 减慢弹簧效果
  accelX *= damping;
  accelY *= damping;

  // 根据整体加速度变化曲线紧密度；
  // 使用 abs() 来避免依赖于加速度方向
  organicConstant = 1 - (abs(accelX) + abs(accelY)) * 0.1;

  // 移动节点
  for (let i = 0; i < nodes; i++) {
    nodeX[i] = nodeStartX[i] + sin(angle[i]) * (accelX * 2);
    nodeY[i] = nodeStartY[i] + sin(angle[i]) * (accelY * 2);
    angle[i] += frequency[i];
  }
}

function setup() {
  createCanvas(710, 400);
  pixelDensity(1);
  describe('多彩渲染曼德尔布罗集。');
  background(0);

  // 在复平面上建立一个值的范围
  // 不同的宽度值改变缩放级别
  let w = 4;
  let h = (w * height) / width;

  // 从负的半宽度和半高度开始
  let xMin = -w / 2;
  let yMin = -h / 2;

  // 访问 pixels[] 数组
  loadPixels();

  // 设置每个点的最大迭代次数
  let maxIterations = 100;

  // x 从 xMin 到 xMax
  let xMax = xMin + w;

  // y 从 yMin 到 yMax
  let yMax = yMin + h;

  // 计算每个像素的 x, y 增量
  let dx = (xMax - xMin) / width;
  let dy = (yMax - yMin) / height;

  // 从 y 开始
  let y = yMin;
  for (let j = 0; j < height; j += 1) {
    // 从 x 开始
    let x = xMin;
    for (let i = 0; i < width; i += 1) {
      // 测试 z = z^2 + cm 的迭代是否发散
      let a = x;
      let b = y;
      let iterations = 0;
      while (iterations < maxIterations) {
        let aSquared = pow(a, 2);
        let bSquared = pow(b, 2);
        let twoAB = 2.0 * a * b;
        a = aSquared - bSquared + x;
        b = twoAB + y;

        // 如果值太大，则停止迭代
        if (dist(aSquared, bSquared, 0, 0) > 16) {
          break;
        }
        iterations += 1;
      }

      // 根据到达无穷大的时间为每个像素着色

      let index = (i + j * width) * 4;

      // 将迭代次数映射到 0-1 范围
      let normalized = map(iterations, 0, maxIterations, 0, 1);

      // 使用归一化值的平方根进行颜色插值
      let lerpAmount = sqrt(normalized);

      // 设置默认颜色为黑色
      let pixelColor = color(0);

      // 蓝色
      let startColor = color(47, 68, 159);

      // 浅黄色
      let endColor = color(255, 255, 128);

      // 如果迭代次数小于最大值，插值颜色
      if (iterations < maxIterations) {
        pixelColor = lerpColor(startColor, endColor, lerpAmount);
      }

      // 将颜色的 RGBA 值复制到像素中
      for (let i = 0; i < 4; i += 1) {
        pixels[index + i] = pixelColor.levels[i];
      }

      x += dx;
    }
    y += dy;
  }
  updatePixels();
}
